import { Form, Input, Button, Select } from "antd";
import { useHistory } from "react-router-dom";
import axios from "axios";
import React, { useState, useEffect } from "react";

const UpdateProduct = ({ location }) => {
    console.log(location.state);
    return (

        <Form
          name="basic"
          labelCol={{
            span: 8
          }}
          wrapperCol={{
            span: 16
          }}
          initialValues={{
            remember: true
          }}
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
          style={{ margin: "0 auto", width: 400 }}
        >
          <Form.Item
            label="Product Name"
            name="name"
            rules={[
              {
                required: true,
                message: "Please enter a product name!"
              }
            ]}
          >
            <Input onChange={handleChange} name="name" value={credentials.name} placeholder={location.name} />
          </Form.Item>

          <Form.Item
            label="Price"
            name="price"
            rules={[
              {
                required: true,
                message: "Please enter a price!"
              }
            ]}
          >
            <Input onChange={handleChange} name="price" value={credentials.price} placeholder={location.price} />

          </Form.Item>
          <Form.Item
            label="Seller"
            name="seller"
            rules={[
              {
                required: true,
                message: "Please select a seller!"
              }
            ]}
          >
            <Button type="primary" htmlType="submit">
              Button
            </Button>
          </Form.Item>
        </Form>
      );

}

export default UpdateProduct;
