import { Form, Input, Button, Select } from "antd";
import { useHistory } from "react-router-dom";
import axios from "axios";
import React, { useState, useEffect } from "react";
import LocalStorageService from "../util/LocalStorageService";
import { getOverflowOptions } from "antd/lib/tooltip/placements";


const AddProduct = () => {

  const [data, setData] = useState();
  const [credentials, setCredentials] = useState({});
  const history = useHistory();

  useEffect(() => {
    fetch();
  }, []);

  fetch = (params = {}) => {
    axios
      .get("http://localhost:8080/admin/sellers", {

      })
      .then((response) => {
        const { data } = response;
        setData(data);
      })

  }



  const onFinish = (values) => {
    console.log("Success:", values);
    console.log("Success name:", credentials.name);
    console.log("Success price:", credentials.price);
    console.log("Success seller:", data.seller);


    axios.get('http://localhost:8080/admin/get-user', {
      username: data.seller
    }).then(response => {
      const { data } = response;
      setData(data);
    })

    axios.post('http://localhost:8080/admin/products/add', {
      name: credentials.name,
      price: credentials.price,
      seller: data
    })
      .then(loginResponse => {
        console.log("Credents:  ", credentials)

      })

  };

  const onFinishFailed = (errorInfo) => {
    console.log("Failed:", errorInfo);
  };

  const handleChange = (event) => {
    setCredentials({
      ...credentials,
      [event.target.name]: event.target.value

    });
  };

  const listSellers = () => {
    const { data } = this.state;

  }

  const getOptions = () => {
    const options = data && data.map((seller) =>
      <Select.Option value={seller.username}>{seller.username}</Select.Option>
    );
    return options;

  }


  return (

    <Form
      name="basic"
      labelCol={{
        span: 8
      }}
      wrapperCol={{
        span: 16
      }}
      initialValues={{
        remember: true
      }}
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
      style={{ margin: "0 auto", width: 400 }}
    >
      <Form.Item
        label="Product Name"
        name="name"
        rules={[
          {
            required: true,
            message: "Please enter a product name!"
          }
        ]}
      >
        <Input onChange={handleChange} name="name" value={credentials.name} />
      </Form.Item>

      <Form.Item
        label="Price"
        name="price"
        rules={[
          {
            required: true,
            message: "Please enter a price!"
          }
        ]}
      >
        <Input onChange={handleChange} name="price" value={credentials.price} />

      </Form.Item>
      <Form.Item
        label="Seller"
        name="seller"
        rules={[
          {
            required: true,
            message: "Please select a seller!"
          }
        ]}
      >
        <Select>
          {getOptions()}

        </Select>
      </Form.Item>

      <Form.Item
        wrapperCol={{
          offset: 8,
          span: 16
        }}
      >
        <Button type="primary" htmlType="submit">
          Add
        </Button>
      </Form.Item>
    </Form>
  );
}


export default AddProduct;
