import React from "react";
import "antd/dist/antd.css";
import { Table } from "antd";
import axios from "axios";

const columns = [
    {
        title: "Name",
        dataIndex: "name",

        sorter: {
            compare: (a, b) => a.name.localeCompare(b.name),
            multiple: 1,
        },
        width: "20%"
    },
    {
        title: "Price",
        dataIndex: "price",
        render: (price) => `$ ${price}`,
        sorter: {
            compare: (a, b) => a.price - b.price,
            multiple: 1,
        },

        width: "20%"
    },
    {
        title: "Seller",
        dataIndex: "seller",
        render: (seller) => `${seller.username}`,
        width: "20%"
    }
];

const getProductParams = (params) => {
    return {
        results: params.pagination.pageSize,
        page: params.pagination.current,
        ...params
    };
};

const addProduct = () => {
    console.log("add product")
    return (
        <Router>

        </Router>
    )
}

class Products extends React.Component {
    state = {
        data: [],
        pagination: {
            current: 1,
            pageSize: 5
        },
        loading: false
    };

    componentDidMount() {
        const { pagination } = this.state;
        this.fetch({ pagination });
    };


    handleTableChange = (pagination, filters, sorter) => {
        this.fetch({
            sortField: sorter.field,
            sortOrder: sorter.order,
            pagination,
            ...filters
        });
    };






    fetch = (params = {}) => {
        this.setState({ loading: true });

        axios
            .get("http://localhost:8080/admin/products", {
                params: getProductParams(params)
            })
            .then((response) => {
                console.log(response.data);
                const { data } = response;

                this.setState({
                    loading: false,
                    data: data,
                    pagination: {
                        ...params.pagination
                    }
                });
            })
            .catch((error) => {
                console.log(error);
            })
            .then(() => {
                this.setState({
                    loading: false
                });
            });
    };




    render() {
        const { data, pagination, loading } = this.state;
        return (
            <div>
                <button id="myButton" class="float-left submit-button" >+</button>

                <script type="text/javascript">
                    document.getElementById("myButton").onclick = function () {
                        location.href = "/sellers"};
                </script>
                <button onClick={addProduct}>
                    +
                </button>

                <Table
                    columns={columns}
                    rowKey={(record) => record.id}
                    dataSource={data}
                    pagination={pagination}
                    loading={loading}
                    onChange={this.handleTableChange}
                />
            </div>

        );
    }
}

export default Products;
