import React from "react";


const Add = () => {
  const [credentials, setCredentials] = useState({});
  const history = useHistory();

    const onFinish = (values) => {
        console.log("Success:", values);

        axios.post('http://localhost:8080/products/add', {
            name: credentials.name,
            price: credentials.price
        })
            .then(loginResponse => {
                console.log(credentials)

            })

    };

    const onFinishFailed = (errorInfo) => {
        console.log("Failed:", errorInfo);
    };

    const handleChange = (event) => {
        setCredentials({
            ...credentials,
            [event.target.name]: event.target.value

        });
    };

    return (
        <Form
            name="basic"
            labelCol={{
                span: 8
            }}
            wrapperCol={{
                span: 16
            }}
            initialValues={{
                remember: true
            }}
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
            style={{ margin: "0 auto", width: 400 }}
        >
            <Form.Item
                label="Product Name"
                name="name"
                rules={[
                    {
                        required: true,
                        message: "Please enter a product name!"
                    }
                ]}
            >
                <Input onChange={handleChange} name="name" value={credentials.name} />
            </Form.Item>

            <Form.Item
                label="Price"
                name="price"
                rules={[
                    {
                        required: true,
                        message: "Please enter a price!"
                    }
                ]}
            >
                <Input onChange={handleChange} name="price" value={credentials.price} />
            </Form.Item>

            <Form.Item
                wrapperCol={{
                    offset: 8,
                    span: 16
                }}
            >
                <Button type="primary" htmlType="submit">
                    Add
                </Button>
            </Form.Item>
        </Form>
    );
}

class AddProduct extends React.Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {
        this.setState({value: event.target.value});
      }

      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
      }

      render() {
        return (
          <form onSubmit={this.handleSubmit}>
            <label>
              Product Name:
              <input type="text" value={this.state.name} onChange={this.handleChange} />
            </label>
            <label>
              Price:
              <input type="text" value={this.state.price} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>
        );
